INSERT INTO learning_objectives_and_skills (code,description,step,field_of_experience,discipline,thematic_unit, created_at, updated_at) VALUES ('EF69AR23', 'Explorar e criar improvisações, composições, arranjos, jingles, trilhas sonoras, entre outros, utilizando vozes, sons corporais e/ou instrumentos acústicos ou eletrônicos, convencionais ou não convencionais, expressando ideias musicais de maneira individual, coletiva e colaborativa.', 'elementary_school', NULL, 'art', 'Música', Now(), now());
INSERT INTO learning_objectives_and_skills (code,description,step,field_of_experience,discipline,thematic_unit, created_at, updated_at) VALUES ('EF69AR24', 'Reconhecer e apreciar artistas e grupos de teatro brasileiros e estrangeiros de diferentes épocas, investigando os modos de criação, produção, divulgação, circulação e organização da atuação profissional em teatro.', 'elementary_school', NULL, 'art', 'Teatro', Now(), now());
