INSERT INTO learning_objectives_and_skills (code,description,step,field_of_experience,discipline,thematic_unit, created_at, updated_at) VALUES ('EF03MA19', 'Estimar, medir e comparar comprimentos, utilizando unidades de medida não padronizadas e padronizadas mais usuais (metro, centímetro e milímetro) e diversos instrumentos de medida.', 'elementary_school', NULL, 'mathematics', 'Grandezas e medidas', Now(), now());
INSERT INTO learning_objectives_and_skills (code,description,step,field_of_experience,discipline,thematic_unit, created_at, updated_at) VALUES ('EF03MA20', 'Estimar e medir capacidade e massa, utilizando unidades de medida não padronizadas e padronizadas mais usuais (litro, mililitro, quilograma, grama e miligrama), reconhecendo-as em leitura de rótulos e embalagens, entre outros.', 'elementary_school', NULL, 'mathematics', 'Grandezas e medidas', Now(), now());
INSERT INTO learning_objectives_and_skills (code,description,step,field_of_experience,discipline,thematic_unit, created_at, updated_at) VALUES ('EF03MA21', 'Comparar, visualmente ou por superposição, áreas de faces de objetos, de figuras planas ou de desenhos.', 'elementary_school', NULL, 'mathematics', 'Grandezas e medidas', Now(), now());
