INSERT INTO learning_objectives_and_skills (code,description,step,field_of_experience,discipline,thematic_unit, created_at, updated_at) VALUES ('EF04MA11', 'Identificar regularidades em sequências numéricas compostas por múltiplos de um número natural.', 'elementary_school', NULL, 'mathematics', 'Álgebra', Now(), now());
